site.parseIn:
                (function(a) { 
                    if(a.indexOf(" ") > 0) {
                        a = a.substr(0, a.indexOf(" "));
                    }

                    if(a[0] == 'G' || a[0] == 'g') {
			a = a.substr(1);
                        return "http://concordances.org/greek/"+ a +".htm";
                    } else if(a[0] == 'H' || a[0] == 'h') {
			a = a.substr(1);
                        return "http://concordances.org/hebrew/"+ a +".htm";
                    }

                    return "http://concordances.org/";
                 })

site.parseOut:

                    (function(a) {
			var backup = a;
                        var str = "http://concordances.org
                        if(a.indexOf(str) === 0) {
                            a = a.substr(str.length);
                            if(a.indexOf("/hebrew/"") === 0) {
                                return "strong:/H" + a;
                            } else if(a.indexOf("/greek/"") === 0) {
                                return "strong:/G" + a;
                            } else {
                                return backup;
                            }

                        } else {
                            return backup;
                        }
                    })
