site.parseInBook:
(function(bookID, bookName) {
    if(bookID == 18) {
        if(chapter < 75)
            bookName = "psalms-1-75";
        else 
            bookName = "psalms-76-150";
    }
        return "http://www.biblestudytools.com/commentaries/gills-exposition-of-the-bible/"+bookName+"/";
 })

site.parseInChapter:
(function(bookID, bookName, chapter) { 
    if(bookID == 18) {
        if(chapter < 75)
            bookName = "psalms-1-75";
        else 
            bookName = "psalms-76-150";
    }
    return "http://www.biblestudytools.com/commentaries/gills-exposition-of-the-bible/"+bookName+"-"+(chapter+1)+"/";
 })

site.parseInVerse:
(function(bookID, bookName, chapter, verse) {
    if(bookID == 18) {
        if(chapter < 75)
            bookName = "psalms-1-75";
        else 
            bookName = "psalms-76-150";
    }
    return "http://www.biblestudytools.com/commentaries/gills-exposition-of-the-bible/"+bookName+"-"+(chapter+1)+"-"+(verse+1)+".html";
 })

site.parseOut:

(function(a) { 
    var backup = a;
    var str3 = "http://www.biblestudytools.com/";
    var str2 = "http://www.biblestudytools.com/bible/passage.aspx?q="

    if(a.indexOf(str2) === 0) {
        a = a.substr(str2.length);
        a = a.replace("+", " ");
        a = a.replace(".html", "");
        return "uinput:/" + a;
    } else if(a.indexOf(str3) === 0) {
        a = a.substr(str3.length);
        if(a.match(/(\d?)(\w+)\/(\d+)-(\d+).html/)) {
            a = a.replace("/", " ");
            a = a.replace(/(\d+)-(\d+)/, "$1:$2");
            a = a.replace(".html", "");
            return "uinput:/" + a;
        } else {
            return backup;
        }
    } else {
        return a;
    }
})
