site.parseIn:
                (function(a) { 
                    if(a.indexOf(" ") > 0) {
                        a = a.substr(0, a.indexOf(" "));
                    }
                    if(a[0] == 'G' || a[0] == 'g') {
                        a = a.substr(1);
			return "http://www.studylight.org/lex/grk/gwview.cgi?n="+a;
                    } else if(a[0] == 'H' || a[0] == 'h') {
                        a = a.substr(1);
			return "http://www.studylight.org/lex/heb/hwview.cgi?n="+a;
                    }
		    return "http://www.studylight.org/";
                 })
